import { useEffect, useState } from "react";
import { Socket } from "socket.io-client";
import { Modal, Button } from "react-bootstrap";
import {
  ClientToServerEvents,
  ServerToClientEvents,
} from "../../../../server/lib/events";
import { BrewController } from "../../../../server/lib/models/controllerModels";

interface Props {
  controller?: BrewController;
  socket?: Socket<ServerToClientEvents, ClientToServerEvents>;
  show: boolean;
}

const initialController: BrewController = {
  id: "noid",
  name: "New controller",
  sensorAddress: "NA",
  heaterPin: -1,
  description: "",
};

export function ControllerModal({ controller, socket, show }: Props) {
  const [status, setStatus] = useState<"new" | "edit">("new");
  const [saveController, setSaveController] =
    useState<BrewController>(initialController);
  const [isOpen, setIsOpen] = useState<boolean>(false);

  useEffect(() => {
    if (controller) {
      setStatus("edit");
      setSaveController(controller);
    } else {
      setStatus("new");
      setSaveController({ ...initialController });
    }
  }, []);

  useEffect(() => {
    console.log("setting modal open");
    setIsOpen(show);
  }, [show]);

  const handleSubmit = () => {
    // process saveController
    if (socket === undefined) {
      console.error("Cannot emit save event: Socket not defined");
      return;
    }
    if (status === "new") {
      const newController: Omit<BrewController, "id"> = {
        name: saveController.name,
        description: saveController.description,
        heaterPin: saveController.heaterPin,
        sensorAddress: saveController.sensorAddress,
      };
      socket.emit("controller:add", newController, (res) => {
        console.log(res);
      });
    }
  };

  function closeModal() {
    setIsOpen(false);
  }

  function openModel() {
    setIsOpen(true);
  }

  return (
    <div className="d-flex justify-content-center">
      <Button variant="primary" onClick={openModel}>
        Add New Controller
      </Button>

      <Modal show={isOpen} onHide={closeModal}>
        <Modal.Header closeButton>
          <Modal.Title>
            {status === "edit" ? "Edit Controller" : "Add New Controller"}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form id="add-edit-controller-form" onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label">Name</label>
              <input
                type="text"
                value={saveController.name}
                onChange={(e) =>
                  setSaveController({
                    ...saveController,
                    name: e.target.value,
                  })
                }
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Description</label>
              <input
                type="text"
                value={saveController.description}
                onChange={(e) =>
                  setSaveController({
                    ...saveController,
                    description: e.target.value,
                  })
                }
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Sensor Address</label>
              <input
                type="text"
                value={saveController.sensorAddress}
                onChange={(e) =>
                  setSaveController({
                    ...saveController,
                    sensorAddress: e.target.value,
                  })
                }
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Heater Pin</label>
              <input
                type="text"
                value={saveController.heaterPin}
                onChange={(e) =>
                  setSaveController({
                    ...saveController,
                    heaterPin: -1,
                  })
                }
              />
            </div>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>
            Close
          </Button>
          <button
            type="submit"
            form="add-edit-controller-form"
            className="btn btn-primary"
          >
            Save changes
          </button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
