import {
  ServerToClientEvents,
  ClientToServerEvents,
} from "../../../server/lib/events";
import { Socket, io } from "socket.io-client";
import { createContext, useEffect, useState } from "react";
const ENDPOINT = "http://localhost:3001";

interface WebSocketContext {
  socket: Socket<ServerToClientEvents, ClientToServerEvents> | undefined;
  status: "Not Connected" | "Connected" | "Connecting";
}

const initialContext: WebSocketContext = {
  socket: undefined,
  status: "Not Connected",
};

export const WebSocketContext = createContext<WebSocketContext>(initialContext);

interface Props {
  children: React.ReactNode;
}

export function WebSocketContextProvider({ children }: Props) {
  const [webSocketState, setWebSocketState] =
    useState<WebSocketContext>(initialContext);

  useEffect(() => {
    const socket: Socket<ServerToClientEvents, ClientToServerEvents> =
      io(ENDPOINT);
    socket.on("connect", () => {
      console.log(`Connected to websocket with id ${socket.id}`);
      setWebSocketState({ ...webSocketState, status: "Connected" });
    });
    setWebSocketState({ socket: socket, status: "Connecting" });
  }, []);

  return (
    <WebSocketContext.Provider value={webSocketState}>
      {children}
    </WebSocketContext.Provider>
  );
}
