import { BrewController } from "../../../../server/lib/models/controllerModels";

interface Props {
  controller: BrewController;
}

export function ControllerCard({ controller }: Props) {
  return (
    <div className="card">
      <div className="card-body">
        <div className="row">
          <div className=""></div>
          <h5 className="card-title">Controller Name: {controller.name}</h5>
          <p className="card-text">Heater Pin: {controller.heaterPin}</p>
          <p className="card-text">
            Sensor Address: {controller.sensorAddress}
          </p>
        </div>
      </div>
    </div>
  );
}
