import { BrewController } from "../../../../server/lib/models/controllerModels";

interface Props {
  controller: BrewController;
}

export function ControllerCard({ controller }: Props) {
  return (
    <div className="card">
      <div className="card-body">
        <h1>Controller Name: {controller.name}</h1>
        <h3>Heater Pin: {controller.heaterPin}</h3>
        <h3>Sensor Address: {controller.sensorAddress}</h3>
      </div>
    </div>
  );
}
