import { useEffect } from "react";
import { BrewController } from "../../../../server/lib/models/controllerModels";
import { ControllerForm } from "./ControllerForm";

interface Props {
  controllers: BrewController[];
}

export function ControllerPanel({ controllers }: Props) {
  useEffect(() => {
    console.log(controllers.length);
  });

  const controllerForms = controllers.map((controller) => {
    return <ControllerForm key={controller.id} controller={controller} />;
  });
  return <div>{controllerForms}</div>;
}
