import { useContext, useEffect, useState } from "react";
import { Socket } from "socket.io-client";
import {
  ClientToServerEvents,
  ServerToClientEvents,
} from "../../../../server/lib/events";
import {
  BrewController,
  ControllerTemperature,
  PowerLevelAdjustmentData,
} from "../../../../server/lib/models/controllerModels";
import { WebSocketContext } from "../../context/websocketContext";
import { PowerLevelComponent } from "./HeatControl";
import { TemperatureDisplay } from "./TempDisplay";

interface Props {
  controller: BrewController;
  socket: Socket<ServerToClientEvents, ClientToServerEvents> | undefined;
}

// const initialState = new BrewControllerState();

export function BrewPanel(props: Props) {
  const { controller, socket } = props;
  const units = "F";
  // const { socket, status } = useContext(WebSocketContext);
  const [powerLevel, setPowerLevel] = useState<number>(0);
  const [temp, setTemp] = useState<ControllerTemperature>("--");

  useEffect(() => {
    console.log("Brew panel says socket is " + socket);
    if (socket !== undefined) {
      socket.on("connect", () => {
        console.log("connect detected on brew panel");
      });
      socket.on("brew:update", (updatedStates) => {
        console.log("Brew panel says brew update detected " + updatedStates);
        // const updatedState = updatedStates.get(controller.id);
        // if (updatedState !== undefined) {
        //   setTemp(updatedState.temperature);
        //   setPowerLevel(updatedState.powerLevel);
        // }
      });
    }
  }, [socket]);

  const adjustPowerLevel = (adjustment: number) => {
    const updatedController: PowerLevelAdjustmentData = {
      id: controller.id,
      powerLevel,
    };
    updatedController.powerLevel += adjustment;
    if (updatedController.powerLevel > 100) updatedController.powerLevel = 100;
    else if (updatedController.powerLevel < 0) updatedController.powerLevel = 0;
    if (socket !== undefined) {
      socket.emit("brew:adjust", updatedController, (res) => {
        if (res !== undefined) {
          if ("error" in res) {
            console.log(res.error);
            return;
          }
          const newPower = res.data;
          console.log(`Power level set to ${newPower.powerLevel}`);
          setPowerLevel(res.data.powerLevel);
        }
      });
    }
  };

  return (
    <div className="d-inline-flex flex-column p-2 border ">
      <TemperatureDisplay temperature={temp} units={units} />
      <PowerLevelComponent
        powerLevel={powerLevel}
        adjustPowerLevel={adjustPowerLevel}
      />
    </div>
  );
}
