import { useEffect, useState } from "react";
import { Modal, Button } from "react-bootstrap";
import { BrewController } from "../../../../server/lib/models/controllerModels";

interface Props {
  controller: BrewController | undefined;
  show: boolean;
  onSubmit: (controller: BrewController) => void;
  closeModal: () => void;
}

const initialController: BrewController = {
  id: "noid",
  name: "New controller",
  sensorAddress: "NA",
  heaterPin: -1,
  description: "",
};

export function ControllerModal({
  controller,
  show,
  onSubmit,
  closeModal,
}: Props) {
  const [saveController, setSaveController] =
    useState<BrewController>(initialController);

  useEffect(() => {
    if (show) {
      setSaveController();
    }
  }, [show]);

  return (
    <div className="d-flex justify-content-center">
      <Modal show={show} onHide={closeModal}>
        <Modal.Header closeButton>
          <Modal.Title>
            {status === "edit" ? "Edit Controller" : "Add New Controller"}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form
            id="add-edit-controller-form"
            onSubmit={() => onSubmit(saveController)}
          >
            <div className="mb-3">
              <label className="form-label">Name</label>
              <input
                type="text"
                value={saveController.name}
                onChange={(e) =>
                  setSaveController({
                    ...saveController,
                    name: e.target.value,
                  })
                }
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Description</label>
              <input
                type="text"
                value={saveController.description}
                onChange={(e) =>
                  setSaveController({
                    ...saveController,
                    description: e.target.value,
                  })
                }
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Sensor Address</label>
              <input
                type="text"
                value={saveController.sensorAddress}
                onChange={(e) =>
                  setSaveController({
                    ...saveController,
                    sensorAddress: e.target.value,
                  })
                }
              />
            </div>
            <div className="mb-3">
              <label className="form-label">Heater Pin</label>
              <input
                type="text"
                value={saveController.heaterPin}
                onChange={(e) =>
                  setSaveController({
                    ...saveController,
                    heaterPin: -1,
                  })
                }
              />
            </div>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeModal}>
            Close
          </Button>
          <button
            type="submit"
            form="add-edit-controller-form"
            className="btn btn-primary"
          >
            Save changes
          </button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}
