// import { stat } from "fs";
import { useEffect, useState } from "react";
import { connect, ConnectedProps } from "react-redux";
import { BrewController } from "../../models/controller.model";
import { RootState } from "../../redux/store";
// import Spinner from "../common/Spinner";
import { io, Socket } from "socket.io-client";
import {
  ClientToServerEvents,
  ServerToClientEvents,
} from "../../../../server/lib/events";
const ENDPOINT = "http://localhost:3001";

const ConfigPage = function (props: Props) {
  const [socket, setSocket] = useState<
    Socket<ServerToClientEvents, ClientToServerEvents> | undefined
  >();

  const [brewControllers, setBrewControllers] = useState<BrewController[]>();

  useEffect(() => {
    console.log("setSocket useEffect");
    const newSocket: Socket<ServerToClientEvents, ClientToServerEvents> =
      io(ENDPOINT);
    setSocket(newSocket);
  }, [setSocket]);

  useEffect(() => {
    console.log("socket useEffect = " + socket);
    if (socket !== undefined) {
      socket.emit("controller:list", (res) => {
        console.log(res);
      });
    }
  }, [socket]);

  // const dispatch = useAppDispatch();

  // const components = props.brewComponents;

  // const handleChange = (event: FormEvent<HTMLInputElement>) => {
  //   const component = {
  //     ...state.component,
  //     componentName: event.currentTarget.value,
  //   };
  //   setState({ component });
  // };

  // const handleSubmit = (event: FormEvent<HTMLFormElement>) => {
  //   event.preventDefault();
  //   dispatch(add(state.component));
  //   alert(state.component.componentName);
  // }

  const RenderedComponents = () => (
    <div>
      {brewControllers ? (
        brewControllers.map((controller) => (
          <div key={controller.name}>Name: {controller.name}</div>
        ))
      ) : (
        <h3>No controllers</h3>
      )}
    </div>
  );

  function handleClick() {
    //
  }

  return (
    <div>
      <div>This is the config page.</div>
      <button onClick={handleClick}>Test</button>
      <RenderedComponents />
      {/* <form>
        <input
          type="text"
          onChange={handleChange}
          value={state.component.componentName}
        />
        <input type="submit" value="Save" />
      </form> */}
    </div>
  );
};

const mapState = (state: RootState) => ({
  brewControllers: state.brewControllerSlice.brewControllers,
});

const connector = connect(mapState);

type PropsFromRedux = ConnectedProps<typeof connector>;

interface Props extends PropsFromRedux {
  // myToogle: boolean
}

export default connector(ConfigPage);
