import { useEffect, useState } from "react";
import { Socket } from "socket.io-client";
import {
  ClientToServerEvents,
  ServerToClientEvents,
} from "../../../../server/lib/events";
import { BrewController } from "../../../../server/lib/models/controllerModels";

interface Props {
  controller?: BrewController;
  socket?: Socket<ServerToClientEvents, ClientToServerEvents>;
}

const initialController: BrewController = {
  id: "noid",
  name: "New controller",
  sensorAddress: "NA",
  heaterPin: -1,
  description: "",
};

export function ControllerModal({ controller, socket }: Props) {
  const [status, setStatus] = useState<"new" | "edit">("new");
  const [saveController, setSaveController] =
    useState<BrewController>(initialController);

  useEffect(() => {
    if (controller) {
      setStatus("edit");
      setSaveController(controller);
    } else {
      setStatus("new");
      setSaveController({ ...initialController });
    }
  }, []);

  const handleSubmit = () => {
    // process saveController
    if (socket === undefined) {
      console.error("Cannot emit save event: Socket not defined");
      return;
    }
    if (status === "new") {
      const newController: Omit<BrewController, "id"> = {
        name: saveController.name,
        description: saveController.description,
        heaterPin: saveController.heaterPin,
        sensorAddress: saveController.sensorAddress,
      };
      debugger;
      socket.emit("controller:add", newController, (res) => {
        console.log(res);
      });
    }
  };

  return (
    <div className="d-flex justify-content-center">
      {/* <!-- Button trigger modal --> */}
      <button
        type="button"
        className="btn btn-primary m-2"
        data-bs-toggle="modal"
        data-bs-target="#exampleModal"
      >
        Add New Controller
      </button>

      {/* <!-- Modal --> */}
      <div
        className="modal fade"
        id="exampleModal"
        tabIndex={-1}
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">
                {status === "edit" ? "Edit Controller" : "Add New Controller"}
              </h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body">
              <form id="add-edit-controller-form" onSubmit={handleSubmit}>
                <div className="mb-3">
                  <label className="form-label">Name</label>
                  <input
                    type="text"
                    value={saveController.name}
                    onChange={(e) =>
                      setSaveController({
                        ...saveController,
                        name: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Description</label>
                  <input
                    type="text"
                    value={saveController.description}
                    onChange={(e) =>
                      setSaveController({
                        ...saveController,
                        description: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Sensor Address</label>
                  <input
                    type="text"
                    value={saveController.sensorAddress}
                    onChange={(e) =>
                      setSaveController({
                        ...saveController,
                        sensorAddress: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Heater Pin</label>
                  <input
                    type="text"
                    value={saveController.heaterPin}
                    onChange={(e) =>
                      setSaveController({
                        ...saveController,
                        heaterPin: -1,
                      })
                    }
                  />
                </div>
              </form>
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Close
              </button>
              <button
                type="submit"
                form="add-edit-controller-form"
                className="btn btn-primary"
              >
                Save changes
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
