import { useContext, useEffect, useState } from "react";
import { BrewControllerState } from "../../../../server/lib/models/controllerModels";
import { WebSocketContext } from "../../context/websocketContext";
import { PowerLevelComponent } from "./HeatControl";
import { TemperatureDisplay } from "./TempDisplay";

interface Props {
  controller: BrewControllerState;
}

// const initialState = new BrewControllerState();

export function BrewPanel(props: Props) {
  const { controller } = props;
  const units = "F";
  const { socket } = useContext(WebSocketContext);
  const [controllerState, setControllerState] =
    useState<BrewControllerState>(controller);

  useEffect(() => {
    const updatedController: BrewControllerState = {
      ...controller,
      powerLevel: controllerState.powerLevel,
    };
    setControllerState(updatedController);
  }, [controller]);

  const adjustPowerLevel = (adjustment: number) => {
    const updatedController = { ...controller };
    updatedController.powerLevel += adjustment;
    if (updatedController.powerLevel > 100) updatedController.powerLevel = 100;
    else if (updatedController.powerLevel < 0) updatedController.powerLevel = 0;
    if (socket !== undefined) {
      socket.emit("brew:adjust", updatedController, (res) => {
        debugger;
        if (res !== undefined) {
          if ("error" in res) {
            console.log(res.error);
            return;
          }
          const update: BrewControllerState = {
            ...controllerState,
            powerLevel: res.data.powerLevel,
          };
          console.log(`Power level set to ${update.powerLevel}`);
          setControllerState(update);
        }
      });
    }
  };

  return (
    <div className="d-inline-flex flex-column p-2 border ">
      <TemperatureDisplay
        temperature={controllerState.temperature}
        units={units}
      />
      <PowerLevelComponent
        powerLevel={controllerState.powerLevel}
        adjustPowerLevel={adjustPowerLevel}
      />
    </div>
  );
}
