// import { stat } from "fs";
import { useContext, useEffect, useState } from "react";
import { BrewController } from "../../../../server/lib/models/controllerModels";
// import Spinner from "../common/Spinner";
import { WebSocketContext } from "../../context/websocketContext";
import Spinner from "../common/Spinner";
import { ControllerPanel } from "./ControllersPanel";

const testInitialControllers: BrewController[] = [
  {
    id: "test",
    name: "name",
    description: "This is a test controller",
    sensorAddress: "12345",
    heaterPin: 3,
  },
];

export const ConfigPage = function () {
  const [brewControllers, setBrewControllers] = useState<BrewController[]>(
    testInitialControllers
  );
  const [isLoaded, setIsLoaded] = useState<boolean>(false);
  const { socket, status } = useContext(WebSocketContext);

  useEffect(() => {
    if (socket !== undefined && status === "Connected") {
      socket.emit("controller:list", (res) => {
        if ("error" in res) {
          console.log(res.error);
        } else {
          setBrewControllers(res.data);
          setIsLoaded(true);
        }
      });
    }
  }, [socket, status]);

  useEffect(() => {
    console.log("first is " + brewControllers[0].name);
  }, [brewControllers]);

  interface Props {
    controllers: BrewController[];
  }

  const RenderedComponents = ({ controllers }: Props) => (
    <div>
      {controllers ? (
        controllers.map((controller) => (
          <div key={controller.name}>Name: {controller.name}</div>
        ))
      ) : (
        <h3>No controllers</h3>
      )}
    </div>
  );

  function handleClick() {
    //
    if (socket !== undefined && status === "Connected") {
      console.log("trying to update with " + brewControllers.length);
      socket.emit("controller:update", brewControllers, (res) => {
        console.log(res);
      });
    }
  }

  return (
    <div>
      <h1 className="text-center">Brewery Configuration!</h1>
      <div className="d-flex justify-content-center">
        {isLoaded && brewControllers !== undefined ? (
          <ControllerPanel controllers={brewControllers} />
        ) : (
          <div>
            <Spinner />
            <h2>Loading ...</h2>
          </div>
        )}
      </div>
      <ControllerPanel controllers={brewControllers} />
    </div>
  );
};
