import { BreweryPanels } from "./BreweryPanels";
import { useContext, useEffect, useState } from "react";
import Spinner from "../common/Spinner";
import {
  BrewController,
  BrewControllerState,
} from "../../../../server/lib/models/controllerModels";
import { WebSocketContext } from "../../context/websocketContext";

export const BrewPage = () => {
  const [brewControllers, setBrewControllers] = useState<
    BrewController[] | undefined
  >(undefined);
  const { socket, status } = useContext(WebSocketContext);
  const [isLoaded, setIsLoaded] = useState<boolean>(false);

  useEffect(() => {
    if (socket !== undefined && status == "Connected") {
      console.log("Starting brew ...");
      socket.emit("brew:start", (res) => {
        if (res !== undefined) {
          if ("error" in res) {
            console.log(res.error);
          } else {
            setBrewControllers(res.data);
          }
        }
      });
    }
  }, [socket]);

  useEffect(() => {
    if (!isLoaded && brewControllers !== undefined) {
      setIsLoaded(true);
    }
  }, [brewControllers]);

  return (
    <>
      <h1 className="text-center">Brew!</h1>
      {/* <BrewerySwitchBar breweryStatus={brewerySettings.status} /> */}
      <div className="d-flex justify-content-center">
        {isLoaded && brewControllerState !== undefined ? (
          <BreweryPanels brewControllerState={brewControllerState} />
        ) : (
          <div>
            <Spinner />
            <h2>Loading ...</h2>
          </div>
        )}
      </div>
    </>
  );
};
