import { useEffect, useState } from "react";
import { BrewController } from "../../../../server/lib/models/controllerModels";

interface Props {
  controller?: BrewController;
}

const initialController: BrewController = {
  id: "noid",
  name: "New controller",
  sensorAddress: "NA",
  heaterPin: -1,
  description: "",
};

export function ControllerModal({ controller }: Props) {
  const [status, setStatus] = useState<"new" | "edit">("new");
  const [saveController, setSaveController] =
    useState<BrewController>(initialController);

  useEffect(() => {
    if (controller) {
      setStatus("edit");
      setSaveController(controller);
    } else {
      setStatus("new");
      setSaveController({ ...initialController });
    }
  }, []);
  return (
    <div className="d-flex justify-content-center">
      {/* <!-- Button trigger modal --> */}
      <button
        type="button"
        className="btn btn-primary "
        data-bs-toggle="modal"
        data-bs-target="#exampleModal"
      >
        Add New Controller
      </button>

      {/* <!-- Modal --> */}
      <div
        className="modal fade"
        id="exampleModal"
        tabIndex={-1}
        aria-labelledby="exampleModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">
                {status === "edit" ? "Edit Controller" : "Add New Controller"}
              </h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body">
              <form>
                <div className="mb-3">
                  <label className="form-label">Name</label>
                  <input
                    type="text"
                    value={saveController.name}
                    onChange={(e) =>
                      setSaveController({
                        ...saveController,
                        name: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Description</label>
                  <input
                    type="text"
                    value={saveController.description}
                    onChange={(e) =>
                      setSaveController({
                        ...saveController,
                        description: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Sensor Address</label>
                  <input
                    type="text"
                    value={saveController.sensorAddress}
                    onChange={(e) =>
                      setSaveController({
                        ...saveController,
                        sensorAddress: e.target.value,
                      })
                    }
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Heater Pin</label>
                  <input
                    type="text"
                    value={saveController.heaterPin}
                    onChange={(e) =>
                      setSaveController({
                        ...saveController,
                        heaterPin: -1,
                      })
                    }
                  />
                </div>
              </form>
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Close
              </button>
              <button type="button" className="btn btn-primary">
                Save changes
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
