import { BreweryPanels } from "./BreweryPanels";
import { useContext, useEffect, useState } from "react";
import Spinner from "../common/Spinner";
import { BrewControllerState } from "../../../../server/lib/models/controllerModels";
import { WebSocketContext } from "../../context/websocketContext";

export const BrewPage = () => {
  const [brewControllerState, setBrewControllerState] = useState<
    BrewControllerState[] | undefined
  >(undefined);
  const { socket, status } = useContext(WebSocketContext);
  const [isLoaded, setIsLoaded] = useState<boolean>(false);

  useEffect(() => {
    if (socket !== undefined && status == "Connected") {
      socket.on("brew:update", (res) => {
        console.log("Brew update detected, count as ");
        setBrewControllerState(res);
      });
      console.log("Starting brew ...");
      socket.emit("brew:start");
    }
  }, [socket]);

  useEffect(() => {
    console.log(`brewControllerState set as ${brewControllerState}`);
    // if (!isLoaded && brewControllerState !== undefined) {
    //   setIsLoaded(true);
    // }
  }, [brewControllerState]);

  return (
    <>
      <h1 className="text-center">Brew!</h1>
      {/* <BrewerySwitchBar breweryStatus={brewerySettings.status} /> */}
      <div className="d-flex justify-content-center">
        {isLoaded && brewControllerState !== undefined ? (
          <BreweryPanels brewControllerState={brewControllerState} />
        ) : (
          <div>
            <Spinner />
            <h2>Loading ...</h2>
          </div>
        )}
      </div>
    </>
  );
};
