import { RootState } from "../../redux/store";
import { BreweryPanels } from "./BreweryPanels";
import { connect, ConnectedProps } from "react-redux";
import { useContext, useEffect, useState } from "react";
import { loadBrewControllers } from "../../redux/actions/brewComponentActions";
import Spinner from "../common/Spinner";
import { WebsocketContext } from "../../context/WebsocketContext";
import { BrewControllerState } from "../../../../server/lib/controller-management/controller.model";

const BrewPage = (props: Props) => {
  const [brewControllerState, setBrewControllerState] = useState<
    BrewControllerState[]
  >([]);
  const { socket } = useContext(WebsocketContext);
  const [isLoaded, setIsLoaded] = useState<boolean>(false);
  // const { brewerySettings, loadBrewControllers } = props;

  useEffect(() => {
    if (socket !== undefined) {
      socket.emit("brew:initialize");

      socket.on("brew:update", (newState) => {
        setBrewControllerState(newState);
      });
    }
  }, [socket]);

  // should have props for current state of the brewery
  // if websocket is controlled via a context, the context should be injected here and an additional wrapper may/likely will be required
  // just leave websockets running when navigation to other pages for now at least

  // useEffect(() => {
  //   if (props.brewControllers === "Not Loaded") {
  //     setIsLoaded(false);
  //     loadBrewControllers();
  //   } else {
  //     setBrewControllers(props.brewControllers);
  //     setIsLoaded(true);
  //   }
  // }, [props.brewControllers]);

  // useEffect(() => {
  //   console.log("Update api call here ...");
  // }, [brewControllers]);

  return (
    <>
      <h1 className="text-center">Brew!</h1>
      {/* <BrewerySwitchBar breweryStatus={brewerySettings.status} /> */}
      <div className="d-flex justify-content-center">
        {isLoaded ? (
          <BreweryPanels
            brewControllerState={brewControllerState}
            socket={socket}
          />
        ) : (
          <div>
            <Spinner />
            <h2>Loading ...</h2>
          </div>
        )}
      </div>
    </>
  );
};

const mapStateToProps = (state: RootState) => ({
  brewControllers: state.brewControllerSlice.brewControllers,
  brewerySettings: state.settingsSlice.settings,
  loading: state.apiStatus.apiCallsInProgress > 0,
});

const mapDispatchToProps = {
  loadBrewControllers,
};

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

interface Props extends PropsFromRedux {}

export default connector(BrewPage);
