// import { stat } from "fs";
import { useContext, useEffect, useState } from "react";
import { BrewController } from "../../../../server/lib/models/controllerModels";
// import Spinner from "../common/Spinner";
import { WebSocketContext } from "../../context/websocketContext";

const testInitialControllers: BrewController[] = [
  {
    id: "test",
    name: "name",
    description: "This is a test controller",
    sensorAddress: "12345",
    heaterPin: 3,
  },
];

export const ConfigPage = function () {
  const [brewControllers, setBrewControllers] = useState<BrewController[]>(
    testInitialControllers
  );
  const { socket, status } = useContext(WebSocketContext);

  useEffect(() => {
    if (socket !== undefined && status === "Connected") {
      socket.emit("controller:list", (res) => {
        if ("error" in res) {
          console.log(res.error);
        } else {
          console.log("Setting data");
          setBrewControllers(res.data);
        }
      });
    }
  }, [socket, status]);

  const RenderedComponents = () => (
    <div>
      {brewControllers ? (
        brewControllers.map((controller) => (
          <div key={controller.name}>Name: {controller.name}</div>
        ))
      ) : (
        <h3>No controllers</h3>
      )}
    </div>
  );

  function handleClick() {
    //
    if (socket !== undefined && status === "Connected") {
      console.log("trying to update with " + brewControllers.length);
      socket.emit("controller:update", brewControllers, (res) => {
        console.log(res);
      });
    }
  }

  return (
    <div>
      <div>This is the config page.</div>
      <button onClick={handleClick}>Test</button>
      <RenderedComponents />
      {/* <form>
        <input
          type="text"
          onChange={handleChange}
          value={state.component.componentName}
        />
        <input type="submit" value="Save" />
      </form> */}
    </div>
  );
};
