import { createSlice } from "@reduxjs/toolkit";

export interface ApiStatusState {
  apiCallsInProgress: number
}

const initialState: ApiStatusState = {
  apiCallsInProgress: 0
};

const apiStatusSlice = createSlice({
  name: "settings",
  initialState,
  reducers: {
    beginApiCall: (state) => {
      state.apiCallsInProgress ++;
    },
    apiCallSuccess: (state) => {
        state.apiCallsInProgress --;
    },
    apiCallError: (state) => {
        state.apiCallsInProgress --;
    }
  },
});

export const { beginApiCall, apiCallSuccess, apiCallError } = apiStatusSlice.actions;

export default apiStatusSlice.reducer;
