import { createContext, ReactNode, useEffect, useState } from "react";
import { connect, ConnectedProps } from "react-redux";
import { io, Socket } from "socket.io-client";
import { RootState } from "../redux/store";
import {
  ClientToServerEvents,
  ServerToClientEvents,
} from "../../../server/lib/events";
import { BrewController } from "../../../server/lib/models/controllerModels";

export interface IWebsocketContext {
  status: "Connected" | "Not Connected" | "Connecting";
  controllers: BrewController[];
}

const initialState: IWebsocketContext = {
  status: "Not Connected",
  controllers: [],
};

const WebsocketContext = createContext<IWebsocketContext>(initialState);

interface Props {
  children: ReactNode;
}

function WebSocketProvider({ children }: Props) {
  // create context state, no need to store in redux
  const [websocketState, setWebsocketState] =
    useState<IWebsocketContext>(initialState);
  // debugger;

  useEffect(() => {
    // if socket has not yet been defined, define and connect.  Websocket will reconnect as automatically.
    if (websocketState.socket === undefined) {
      const socket: Socket<ServerToClientEvents, ClientToServerEvents> = io(
        "http://localhost:3001"
      );

      // set up listeners here
      // connected
      socket.on("connect", () => {
        console.log(`Connected to websocket with id: ${socket.id}`);
        setWebsocketState({ ...websocketState, status: "Connected" });
      });

      setWebsocketState({ ...websocketState, status: "Connecting", socket });
    }
  }, []);

  return (
    <WebsocketContext.Provider value={websocketState}>
      {children}
    </WebsocketContext.Provider>
  );
}

const mapStateToProps = (state: RootState) => ({
  websocketStatus: state.websocketStatus.status,
});

const mapDispatchToProps = {};

const connector = connect(mapStateToProps, mapDispatchToProps);

type PropsFromRedux = ConnectedProps<typeof connector>;

interface Props extends PropsFromRedux {}

const wrappedProvider = connector(WebSocketProvider);

export { wrappedProvider as WebsocketContextProvider, WebsocketContext };
